import axios from 'axios';

// Create axios instance with base configuration
const api = axios.create({
  baseURL: process.env.REACT_APP_API_URL || 'http://localhost:8000',
  timeout: 30000,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Request interceptor for logging and auth token
api.interceptors.request.use(
  async (config) => {
    console.log(`API Request: ${config.method?.toUpperCase()} ${config.url}`);

    // Add auth token if available
    const accessToken = localStorage.getItem('accessToken');
    if (accessToken) {
      config.headers.Authorization = `Bearer ${accessToken}`;
    }

    return config;
  },
  (error) => {
    console.error('API Request Error:', error);
    return Promise.reject(error);
  }
);

// Response interceptor for error handling
api.interceptors.response.use(
  (response) => {
    console.log(`API Response: ${response.status} ${response.config.url}`);
    return response;
  },
  (error) => {
    console.error('API Response Error:', error.response?.data || error.message);
    return Promise.reject(error);
  }
);

// Resume Analysis API
export const resumeAPI = {
  // Upload resume file
  uploadResume: async (file) => {
    const formData = new FormData();
    formData.append('file', file);

    const response = await api.post('/api/v1/resume/upload', formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
      },
    });
    return response.data;
  },

  // Get analysis results
  getAnalysis: async (analysisId) => {
    const response = await api.get(`/api/v1/resume/analysis/${analysisId}`);
    return response.data;
  },

  // Get analysis status
  getStatus: async (analysisId) => {
    const response = await api.get(`/api/v1/resume/status/${analysisId}`);
    return response.data;
  },
};

// Dashboard API
export const dashboardAPI = {
  // Get dashboard overview
  getOverview: async () => {
    const response = await api.get('/api/v1/dashboard/overview');
    return response.data;
  },

  // Get all analyses with pagination
  getAnalyses: async (limit = 20, offset = 0) => {
    const response = await api.get('/api/v1/dashboard/analyses', {
      params: { limit, offset },
    });
    return response.data;
  },

  // Get statistics
  getStatistics: async () => {
    const response = await api.get('/api/v1/dashboard/statistics');
    return response.data;
  },

  // Search analyses
  searchAnalyses: async (query, limit = 10) => {
    const response = await api.get('/api/v1/dashboard/search', {
      params: { query, limit },
    });
    return response.data;
  },
};

// Health check
export const healthAPI = {
  checkHealth: async () => {
    const response = await api.get('/health');
    return response.data;
  },
};

export default api;
