import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { ArrowLeft, Clock, CheckCircle, AlertCircle, Star, TrendingUp, Brain, FileText } from 'lucide-react';
import { resumeAPI } from '../services/api/api';

const Analysis = () => {
  const { id } = useParams();
  const [analysis, setAnalysis] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [polling, setPolling] = useState(false);

  useEffect(() => {
    loadAnalysis();
  }, [id]);

  useEffect(() => {
    let interval;
    if (polling && (analysis?.status === 'pending' || analysis?.status === 'processing')) {
      interval = setInterval(checkStatus, 3000);
    }
    return () => clearInterval(interval);
  }, [polling, analysis?.status]);

  const loadAnalysis = async () => {
    try {
      setLoading(true);
      const data = await resumeAPI.getAnalysis(id);
      setAnalysis(data.analysis);

      if (data.analysis.status === 'pending' || data.analysis.status === 'processing') {
        setPolling(true);
      }
    } catch (err) {
      if (err.response?.status === 202) {
        // Analysis still in progress
        setPolling(true);
        checkStatus();
      } else {
        setError('Failed to load analysis');
        console.error('Analysis load error:', err);
      }
    } finally {
      setLoading(false);
    }
  };

  const checkStatus = async () => {
    try {
      const statusData = await resumeAPI.getStatus(id);
      if (statusData.status === 'completed') {
        setPolling(false);
        loadAnalysis(); // Reload full analysis
      } else if (statusData.status === 'failed') {
        setPolling(false);
        setError('Analysis failed');
      }
    } catch (err) {
      console.error('Status check error:', err);
    }
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'completed':
        return <CheckCircle className="w-6 h-6 text-green-500" />;
      case 'processing':
        return <Clock className="w-6 h-6 text-yellow-500 animate-spin" />;
      case 'failed':
        return <AlertCircle className="w-6 h-6 text-red-500" />;
      default:
        return <Clock className="w-6 h-6 text-gray-400" />;
    }
  };

  const getStatusText = (status) => {
    switch (status) {
      case 'completed':
        return 'Analysis Complete';
      case 'processing':
        return 'Processing...';
      case 'failed':
        return 'Analysis Failed';
      default:
        return 'Pending';
    }
  };

  const getLevelColor = (level) => {
    const colors = {
      beginner: 'bg-blue-100 text-blue-800',
      intermediate: 'bg-yellow-100 text-yellow-800',
      advanced: 'bg-orange-100 text-orange-800',
      expert: 'bg-green-100 text-green-800'
    };
    return colors[level] || colors.beginner;
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-64">
        <div className="loading-spinner"></div>
        <span className="ml-3 text-gray-600">Loading analysis...</span>
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-center py-12">
        <AlertCircle className="w-12 h-12 text-red-500 mx-auto mb-4" />
        <h3 className="text-lg font-semibold text-gray-900 mb-2">Error Loading Analysis</h3>
        <p className="text-gray-600 mb-4">{error}</p>
        <Link to="/dashboard" className="btn-primary">
          Back to Dashboard
        </Link>
      </div>
    );
  }

  return (
    <div className="space-y-8">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div className="flex items-center space-x-4">
          <Link to="/dashboard" className="btn-secondary">
            <ArrowLeft className="w-4 h-4 mr-2" />
            Back
          </Link>
          <div>
            <h1 className="text-3xl font-bold text-gray-900">Resume Analysis</h1>
            <p className="text-gray-600">{analysis?.filename}</p>
          </div>
        </div>

        <div className="flex items-center space-x-3">
          {getStatusIcon(analysis?.status)}
          <div>
            <p className="font-medium text-gray-900">{getStatusText(analysis?.status)}</p>
            <p className="text-sm text-gray-600">
              {analysis?.upload_date && formatDate(analysis.upload_date)}
            </p>
          </div>
        </div>
      </div>

      {analysis?.status === 'pending' && (
        <div className="card">
          <div className="text-center py-8">
            <Clock className="w-16 h-16 text-yellow-500 mx-auto mb-4 animate-pulse" />
            <h3 className="text-xl font-semibold text-gray-900 mb-2">Analysis in Progress</h3>
            <p className="text-gray-600 mb-4">
              Our AI is analyzing your resume. This usually takes 30-60 seconds.
            </p>
            <div className="loading-spinner mx-auto"></div>
          </div>
        </div>
      )}

      {analysis?.status === 'completed' && (
        <>
          {/* Summary */}
          <div className="card">
            <div className="flex items-center space-x-3 mb-4">
              <Brain className="w-6 h-6 text-primary-600" />
              <h2 className="text-xl font-semibold text-gray-900">AI Summary</h2>
            </div>
            <p className="text-gray-700 leading-relaxed">{analysis.summary}</p>
          </div>

          {/* Expertise Areas */}
          <div className="card">
            <div className="flex items-center space-x-3 mb-6">
              <TrendingUp className="w-6 h-6 text-primary-600" />
              <h2 className="text-xl font-semibold text-gray-900">Expertise Areas</h2>
            </div>

            <div className="space-y-6">
              {analysis.expertise_areas?.map((expertise, index) => (
                <div key={index} className="border border-gray-200 rounded-lg p-4">
                  <div className="flex justify-between items-start mb-3">
                    <h3 className="text-lg font-medium text-gray-900">{expertise.name}</h3>
                    <span className={`badge ${getLevelColor(expertise.level)}`}>
                      {expertise.level}
                    </span>
                  </div>

                  <p className="text-gray-600 mb-4">{expertise.description}</p>

                  <div className="flex items-center space-x-2">
                    <span className="text-sm text-gray-500">
                      Confidence: {Math.round(expertise.confidence * 100)}%
                    </span>
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Strong Zones */}
          <div className="card">
            <div className="flex items-center space-x-3 mb-6">
              <Star className="w-6 h-6 text-primary-600" />
              <h2 className="text-xl font-semibold text-gray-900">Strong Zones</h2>
            </div>

            <div className="grid md:grid-cols-2 gap-6">
              {analysis.strong_zones?.map((zone, index) => (
                <div key={index} className="border border-gray-200 rounded-lg p-4">
                  <div className="flex justify-between items-start mb-3">
                    <h3 className="text-lg font-medium text-gray-900">{zone.name}</h3>
                    <div className="flex items-center space-x-1">
                      <Star className="w-4 h-4 text-yellow-500 fill-current" />
                      <span className="text-sm font-medium text-gray-700">
                        Impact: {zone.impact}
                      </span>
                    </div>
                  </div>

                  <p className="text-gray-600 mb-3">{zone.description}</p>

                  <div className="space-y-2">
                    <p className="text-sm font-medium text-gray-900">Supporting Evidence:</p>
                    <div className="text-sm text-gray-600 flex items-start">
                      <span className="text-primary-600 mr-2">•</span>
                      {zone.evidence}
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Raw Text (Collapsible) */}
          <div className="card">
            <details className="group">
              <summary className="flex items-center space-x-3 cursor-pointer list-none">
                <FileText className="w-6 h-6 text-primary-600" />
                <h2 className="text-xl font-semibold text-gray-900">Extracted Text</h2>
                <span className="text-sm text-gray-500 group-open:hidden">(Click to expand)</span>
                <span className="text-sm text-gray-500 hidden group-open:inline">(Click to collapse)</span>
              </summary>
              <div className="mt-4 p-4 bg-gray-50 rounded-lg">
                <pre className="text-sm text-gray-700 whitespace-pre-wrap font-sans">
                  {analysis.raw_text}
                </pre>
              </div>
            </details>
          </div>
        </>
      )}
    </div>
  );
};

export default Analysis;
