import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { ArrowLeft, Save, Upload } from 'lucide-react';
import FileUpload from '../components/FileUpload';

const AddResume = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    candidate_name: '',
    candidate_email: '',
    current_role: '',
    experience_years: '',
    location: '',
    phone: '',
    linkedin_url: '',
    notes: ''
  });
  const [isUploading, setIsUploading] = useState(false);
  const [uploadedFile, setUploadedFile] = useState(null);
  const [analysisId, setAnalysisId] = useState(null);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleFileUpload = async (file) => {
    setIsUploading(true);
    setUploadedFile(file);

    try {
      // Upload file to resume analysis endpoint
      const formData = new FormData();
      formData.append('file', file);

      const response = await fetch('http://localhost:8000/api/v1/resume/upload', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Failed to upload resume');
      }

      const result = await response.json();
      setAnalysisId(result.analysis_id);

      // Wait for analysis to complete
      await waitForAnalysis(result.analysis_id);

    } catch (error) {
      console.error('Upload error:', error);
      throw error;
    } finally {
      setIsUploading(false);
    }
  };

  const waitForAnalysis = async (analysisId) => {
    const maxAttempts = 30; // 30 seconds max
    let attempts = 0;

    while (attempts < maxAttempts) {
      try {
        const response = await fetch(`http://localhost:8000/api/v1/resume/status/${analysisId}`);
        const status = await response.json();

        if (status.status === 'completed') {
          return true;
        } else if (status.status === 'failed') {
          throw new Error('Analysis failed');
        }

        // Wait 1 second before next check
        await new Promise(resolve => setTimeout(resolve, 1000));
        attempts++;
      } catch (error) {
        console.error('Status check error:', error);
        throw error;
      }
    }

    throw new Error('Analysis timeout');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!uploadedFile || !analysisId) {
      alert('Please upload a resume first');
      return;
    }

    if (!formData.candidate_name || !formData.candidate_email) {
      alert('Please fill in candidate name and email');
      return;
    }

    try {
      // Get analysis results
      const analysisResponse = await fetch(`http://localhost:8000/api/v1/resume/analysis/${analysisId}`);
      const analysisData = await analysisResponse.json();

      // Add to resume bank
      const resumeBankData = {
        ...formData,
        analysis_id: analysisId,
        filename: uploadedFile.name,
        analysis_results: analysisData.analysis,
        status: 'active'
      };

      const response = await fetch('http://localhost:8000/api/v1/resume-bank/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(resumeBankData),
      });

      if (!response.ok) {
        throw new Error('Failed to add resume to bank');
      }

      alert('Resume added to bank successfully!');
      navigate('/resume-bank');

    } catch (error) {
      console.error('Submit error:', error);
      alert('Failed to add resume to bank: ' + error.message);
    }
  };

  return (
    <div className="max-w-4xl mx-auto">
      {/* Header */}
      <div className="flex items-center justify-between mb-8">
        <div className="flex items-center space-x-4">
          <button
            onClick={() => navigate('/resume-bank')}
            className="flex items-center space-x-2 text-gray-600 hover:text-gray-900 transition-colors"
          >
            <ArrowLeft className="w-5 h-5" />
            <span>Back to Resume Bank</span>
          </button>
        </div>
        <h1 className="text-3xl font-bold text-gray-900">Add Resume to Bank</h1>
      </div>

      <form onSubmit={handleSubmit} className="space-y-8">
        {/* File Upload Section */}
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
          <h2 className="text-xl font-semibold text-gray-900 mb-4 flex items-center">
            <Upload className="w-5 h-5 mr-2" />
            Upload Resume
          </h2>
          <FileUpload onFileUpload={handleFileUpload} isUploading={isUploading} />

          {uploadedFile && (
            <div className="mt-4 p-3 bg-blue-50 border border-blue-200 rounded-lg">
              <p className="text-sm text-blue-700">
                <strong>Uploaded:</strong> {uploadedFile.name}
              </p>
            </div>
          )}
        </div>

        {/* Candidate Information */}
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
          <h2 className="text-xl font-semibold text-gray-900 mb-6">Candidate Information</h2>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <label htmlFor="candidate_name" className="block text-sm font-medium text-gray-700 mb-2">
                Full Name *
              </label>
              <input
                type="text"
                id="candidate_name"
                name="candidate_name"
                value={formData.candidate_name}
                onChange={handleInputChange}
                required
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                placeholder="Enter candidate's full name"
              />
            </div>

            <div>
              <label htmlFor="candidate_email" className="block text-sm font-medium text-gray-700 mb-2">
                Email Address *
              </label>
              <input
                type="email"
                id="candidate_email"
                name="candidate_email"
                value={formData.candidate_email}
                onChange={handleInputChange}
                required
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                placeholder="Enter candidate's email"
              />
            </div>

            <div>
              <label htmlFor="current_role" className="block text-sm font-medium text-gray-700 mb-2">
                Current Role
              </label>
              <input
                type="text"
                id="current_role"
                name="current_role"
                value={formData.current_role}
                onChange={handleInputChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                placeholder="e.g., Software Engineer"
              />
            </div>

            <div>
              <label htmlFor="experience_years" className="block text-sm font-medium text-gray-700 mb-2">
                Years of Experience
              </label>
              <input
                type="number"
                id="experience_years"
                name="experience_years"
                value={formData.experience_years}
                onChange={handleInputChange}
                min="0"
                max="50"
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                placeholder="e.g., 5"
              />
            </div>

            <div>
              <label htmlFor="location" className="block text-sm font-medium text-gray-700 mb-2">
                Location
              </label>
              <input
                type="text"
                id="location"
                name="location"
                value={formData.location}
                onChange={handleInputChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                placeholder="e.g., San Francisco, CA"
              />
            </div>

            <div>
              <label htmlFor="phone" className="block text-sm font-medium text-gray-700 mb-2">
                Phone Number
              </label>
              <input
                type="tel"
                id="phone"
                name="phone"
                value={formData.phone}
                onChange={handleInputChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                placeholder="e.g., +1 (555) 123-4567"
              />
            </div>

            <div className="md:col-span-2">
              <label htmlFor="linkedin_url" className="block text-sm font-medium text-gray-700 mb-2">
                LinkedIn URL
              </label>
              <input
                type="url"
                id="linkedin_url"
                name="linkedin_url"
                value={formData.linkedin_url}
                onChange={handleInputChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                placeholder="https://linkedin.com/in/username"
              />
            </div>

            <div className="md:col-span-2">
              <label htmlFor="notes" className="block text-sm font-medium text-gray-700 mb-2">
                Notes
              </label>
              <textarea
                id="notes"
                name="notes"
                value={formData.notes}
                onChange={handleInputChange}
                rows="4"
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                placeholder="Additional notes about the candidate..."
              />
            </div>
          </div>
        </div>

        {/* Submit Button */}
        <div className="flex justify-end space-x-4">
          <button
            type="button"
            onClick={() => navigate('/resume-bank')}
            className="px-6 py-2 border border-gray-300 rounded-md text-gray-700 hover:bg-gray-50 transition-colors"
          >
            Cancel
          </button>
          <button
            type="submit"
            disabled={!uploadedFile || isUploading}
            className="px-6 py-2 bg-primary-600 text-white rounded-md hover:bg-primary-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors flex items-center space-x-2"
          >
            <Save className="w-4 h-4" />
            <span>Add to Resume Bank</span>
          </button>
        </div>
      </form>
    </div>
  );
};

export default AddResume;
