import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { BarChart3, FileText, Clock, CheckCircle, AlertCircle, Search, TrendingUp } from 'lucide-react';
import { dashboardAPI } from '../services/api/api';

const Dashboard = () => {
  const [overview, setOverview] = useState(null);
  const [analyses, setAnalyses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    loadDashboardData();
  }, []);

  const loadDashboardData = async () => {
    try {
      setLoading(true);
      const [overviewData, analysesData] = await Promise.all([
        dashboardAPI.getOverview(),
        dashboardAPI.getAnalyses(10, 0)
      ]);

      setOverview(overviewData);
      setAnalyses(analysesData.analyses || []);
    } catch (err) {
      setError('Failed to load dashboard data');
      console.error('Dashboard load error:', err);
    } finally {
      setLoading(false);
    }
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'completed':
        return <CheckCircle className="w-5 h-5 text-green-500" />;
      case 'processing':
        return <Clock className="w-5 h-5 text-yellow-500" />;
      case 'failed':
        return <AlertCircle className="w-5 h-5 text-red-500" />;
      default:
        return <Clock className="w-5 h-5 text-gray-400" />;
    }
  };

  const getStatusBadge = (status) => {
    const statusClasses = {
      completed: 'badge-success',
      processing: 'badge-warning',
      failed: 'badge-error',
      pending: 'badge-info'
    };

    return (
      <span className={`badge ${statusClasses[status] || 'badge-info'}`}>
        {status.charAt(0).toUpperCase() + status.slice(1)}
      </span>
    );
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-64">
        <div className="loading-spinner"></div>
        <span className="ml-3 text-gray-600">Loading dashboard...</span>
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-center py-12">
        <AlertCircle className="w-12 h-12 text-red-500 mx-auto mb-4" />
        <h3 className="text-lg font-semibold text-gray-900 mb-2">Error Loading Dashboard</h3>
        <p className="text-gray-600 mb-4">{error}</p>
        <button onClick={loadDashboardData} className="btn-primary">
          Try Again
        </button>
      </div>
    );
  }

  return (
    <div className="space-y-8">
      {/* Header */}
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">Dashboard</h1>
          <p className="text-gray-600">Overview of resume analyses and insights</p>
        </div>
        <Link to="/" className="btn-primary">
          <FileText className="w-4 h-4 mr-2" />
          New Analysis
        </Link>
      </div>

      {/* Statistics Cards */}
      {overview && (
        <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-6">
          <div className="card">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600">Total Analyses</p>
                <p className="text-2xl font-bold text-gray-900">{overview.total_analyses}</p>
              </div>
              <BarChart3 className="w-8 h-8 text-primary-600" />
            </div>
          </div>

          <div className="card">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600">Completed</p>
                <p className="text-2xl font-bold text-green-600">
                  {overview.statistics?.status_distribution?.completed || 0}
                </p>
              </div>
              <CheckCircle className="w-8 h-8 text-green-600" />
            </div>
          </div>

          <div className="card">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600">Processing</p>
                <p className="text-2xl font-bold text-yellow-600">
                  {overview.statistics?.status_distribution?.processing || 0}
                </p>
              </div>
              <Clock className="w-8 h-8 text-yellow-600" />
            </div>
          </div>

          <div className="card">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600">Last 7 Days</p>
                <p className="text-2xl font-bold text-blue-600">
                  {overview.statistics?.recent_activity?.last_7_days || 0}
                </p>
              </div>
              <TrendingUp className="w-8 h-8 text-blue-600" />
            </div>
          </div>
        </div>
      )}

      {/* Search and Recent Analyses */}
      <div className="card">
        <div className="flex justify-between items-center mb-6">
          <h2 className="text-xl font-semibold text-gray-900">Recent Analyses</h2>

          {/* Search */}
          <div className="relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400" />
            <input
              type="text"
              placeholder="Search analyses..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-500"
            />
          </div>
        </div>

        {/* Analyses List */}
        <div className="space-y-4">
          {analyses.length === 0 ? (
            <div className="text-center py-8">
              <FileText className="w-12 h-12 text-gray-400 mx-auto mb-4" />
              <h3 className="text-lg font-medium text-gray-900 mb-2">No analyses yet</h3>
              <p className="text-gray-600 mb-4">Upload your first resume to get started</p>
              <Link to="/" className="btn-primary">
                Upload Resume
              </Link>
            </div>
          ) : (
            analyses
              .filter(analysis => 
                analysis.filename.toLowerCase().includes(searchQuery.toLowerCase()) ||
                analysis.summary?.toLowerCase().includes(searchQuery.toLowerCase())
              )
              .map((analysis) => (
                <div
                  key={analysis.id}
                  className="border border-gray-200 rounded-lg p-4 hover:bg-gray-50 transition-colors"
                >
                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-4">
                      {getStatusIcon(analysis.status)}
                      <div>
                        <h3 className="font-medium text-gray-900">
                          {analysis.filename}
                        </h3>
                        <p className="text-sm text-gray-600">
                          {formatDate(analysis.upload_date)}
                        </p>
                      </div>
                    </div>

                    <div className="flex items-center space-x-4">
                      {getStatusBadge(analysis.status)}

                      {analysis.status === 'completed' && (
                        <Link
                          to={`/analysis/${analysis.id}`}
                          className="btn-secondary text-sm"
                        >
                          View Results
                        </Link>
                      )}
                    </div>
                  </div>

                  {analysis.status === 'completed' && analysis.summary && (
                    <p className="text-sm text-gray-600 mt-2 line-clamp-2">
                      {analysis.summary}
                    </p>
                  )}
                </div>
              ))
          )}
        </div>
      </div>

      {/* Expertise Distribution Chart */}
      {overview?.statistics?.expertise_distribution && (
        <div className="card">
          <h2 className="text-xl font-semibold text-gray-900 mb-6">Expertise Distribution</h2>
          <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-4">
            {Object.entries(overview.statistics.expertise_distribution)
              .sort(([,a], [,b]) => b - a)
              .slice(0, 6)
              .map(([category, count]) => (
                <div key={category} className="flex justify-between items-center p-3 bg-gray-50 rounded-lg">
                  <span className="text-sm font-medium text-gray-700">{category}</span>
                  <span className="text-sm text-gray-600">{count}</span>
                </div>
              ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
