import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { ArrowLeft, Upload, FileText, User, Mail, Phone, MapPin, Calendar, DollarSign, Tag } from 'lucide-react';

const AddResume = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [uploadStatus, setUploadStatus] = useState('idle'); // idle, uploading, analyzing, completed, error
  const [analysisId, setAnalysisId] = useState(null);
  const [candidateInfo, setCandidateInfo] = useState({
    candidate_name: '',
    candidate_email: '',
    candidate_phone: '',
    candidate_location: '',
    years_experience: '',
    current_role: '',
    desired_role: '',
    salary_expectation: '',
    availability: '',
    tags: '',
    notes: ''
  });

  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    setLoading(true);
    setUploadStatus('uploading');

    try {
      const formData = new FormData();
      formData.append('file', file);

      const response = await fetch('http://localhost:8000/api/v1/resume/upload', {
        method: 'POST',
        body: formData,
      });

      if (response.ok) {
        const data = await response.json();
        setAnalysisId(data.analysis_id);
        setUploadStatus('analyzing');

        // Poll for analysis completion
        await pollAnalysisStatus(data.analysis_id);
      } else {
        throw new Error('Failed to upload resume');
      }
    } catch (error) {
      console.error('Error uploading resume:', error);
      setUploadStatus('error');
    } finally {
      setLoading(false);
    }
  };

  const pollAnalysisStatus = async (analysisId) => {
    const maxAttempts = 30; // 30 seconds max
    let attempts = 0;

    const poll = async () => {
      try {
        const response = await fetch(`http://localhost:8000/api/v1/resume/status/${analysisId}`);
        if (response.ok) {
          const data = await response.json();

          if (data.status === 'completed') {
            setUploadStatus('completed');
            return;
          } else if (data.status === 'failed') {
            setUploadStatus('error');
            return;
          }
        }

        attempts++;
        if (attempts < maxAttempts) {
          setTimeout(poll, 1000);
        } else {
          setUploadStatus('error');
        }
      } catch (error) {
        console.error('Error polling status:', error);
        setUploadStatus('error');
      }
    };

    poll();
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!analysisId) {
      alert('Please upload and analyze a resume first');
      return;
    }

    setLoading(true);

    try {
      const resumeData = {
        filename: 'Uploaded Resume',
        candidate_name: candidateInfo.candidate_name,
        candidate_email: candidateInfo.candidate_email,
        candidate_phone: candidateInfo.candidate_phone || null,
        candidate_location: candidateInfo.candidate_location || null,
        years_experience: candidateInfo.years_experience ? parseInt(candidateInfo.years_experience) : null,
        current_role: candidateInfo.current_role || null,
        desired_role: candidateInfo.desired_role || null,
        salary_expectation: candidateInfo.salary_expectation || null,
        availability: candidateInfo.availability || null,
        tags: candidateInfo.tags ? candidateInfo.tags.split(',').map(tag => tag.trim()) : [],
        notes: candidateInfo.notes || null,
        resume_analysis_id: analysisId
      };

      const response = await fetch('http://localhost:8000/api/v1/resume-bank/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(resumeData),
      });

      if (response.ok) {
        alert('Resume added to bank successfully!');
        navigate('/resume-bank');
      } else {
        throw new Error('Failed to add resume to bank');
      }
    } catch (error) {
      console.error('Error adding resume to bank:', error);
      alert('Failed to add resume to bank');
    } finally {
      setLoading(false);
    }
  };

  const getStatusMessage = () => {
    switch (uploadStatus) {
      case 'uploading':
        return 'Uploading resume...';
      case 'analyzing':
        return 'Analyzing resume with AI...';
      case 'completed':
        return 'Analysis completed! Fill in candidate details below.';
      case 'error':
        return 'Error occurred. Please try again.';
      default:
        return 'Upload a resume to get started';
    }
  };

  const getStatusColor = () => {
    switch (uploadStatus) {
      case 'uploading':
      case 'analyzing':
        return 'text-blue-600';
      case 'completed':
        return 'text-green-600';
      case 'error':
        return 'text-red-600';
      default:
        return 'text-gray-600';
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="mb-8">
          <button
            onClick={() => navigate('/resume-bank')}
            className="flex items-center text-gray-600 hover:text-gray-900 mb-4"
          >
            <ArrowLeft className="w-4 h-4 mr-2" />
            Back to Resume Bank
          </button>
          <h1 className="text-3xl font-bold text-gray-900">Add Resume to Bank</h1>
          <p className="text-gray-600 mt-2">Upload a resume and add candidate information to your resume bank</p>
        </div>

        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-8">
          {/* Upload Section */}
          <div className="mb-8">
            <h2 className="text-xl font-semibold text-gray-900 mb-4">Step 1: Upload Resume</h2>

            <div className="border-2 border-dashed border-gray-300 rounded-lg p-8 text-center">
              <Upload className="w-12 h-12 text-gray-400 mx-auto mb-4" />
              <p className={`text-lg font-medium ${getStatusColor()}`}>
                {getStatusMessage()}
              </p>

              {uploadStatus === 'idle' && (
                <div className="mt-4">
                  <label className="cursor-pointer inline-flex items-center px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700">
                    <FileText className="w-4 h-4 mr-2" />
                    Choose PDF File
                    <input
                      type="file"
                      accept=".pdf"
                      onChange={handleFileUpload}
                      className="hidden"
                    />
                  </label>
                  <p className="text-sm text-gray-500 mt-2">Supports PDF files only</p>
                </div>
              )}

              {uploadStatus === 'uploading' && (
                <div className="mt-4">
                  <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto"></div>
                </div>
              )}

              {uploadStatus === 'analyzing' && (
                <div className="mt-4">
                  <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto"></div>
                  <p className="text-sm text-gray-500 mt-2">This may take a few moments...</p>
                </div>
              )}

              {uploadStatus === 'error' && (
                <div className="mt-4">
                  <button
                    onClick={() => {
                      setUploadStatus('idle');
                      setAnalysisId(null);
                    }}
                    className="inline-flex items-center px-4 py-2 bg-red-600 text-white rounded-md hover:bg-red-700"
                  >
                    Try Again
                  </button>
                </div>
              )}
            </div>
          </div>

          {/* Candidate Information Form */}
          {uploadStatus === 'completed' && (
            <form onSubmit={handleSubmit}>
              <h2 className="text-xl font-semibold text-gray-900 mb-4">Step 2: Candidate Information</h2>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                {/* Basic Information */}
                <div className="space-y-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      <User className="w-4 h-4 inline mr-1" />
                      Full Name *
                    </label>
                    <input
                      type="text"
                      required
                      value={candidateInfo.candidate_name}
                      onChange={(e) => setCandidateInfo(prev => ({ ...prev, candidate_name: e.target.value }))}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                      placeholder="John Doe"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      <Mail className="w-4 h-4 inline mr-1" />
                      Email Address *
                    </label>
                    <input
                      type="email"
                      required
                      value={candidateInfo.candidate_email}
                      onChange={(e) => setCandidateInfo(prev => ({ ...prev, candidate_email: e.target.value }))}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                      placeholder="john.doe@example.com"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      <Phone className="w-4 h-4 inline mr-1" />
                      Phone Number
                    </label>
                    <input
                      type="tel"
                      value={candidateInfo.candidate_phone}
                      onChange={(e) => setCandidateInfo(prev => ({ ...prev, candidate_phone: e.target.value }))}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                      placeholder="+1 (555) 123-4567"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      <MapPin className="w-4 h-4 inline mr-1" />
                      Location
                    </label>
                    <input
                      type="text"
                      value={candidateInfo.candidate_location}
                      onChange={(e) => setCandidateInfo(prev => ({ ...prev, candidate_location: e.target.value }))}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                      placeholder="San Francisco, CA"
                    />
                  </div>
                </div>

                {/* Professional Information */}
                <div className="space-y-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      <Calendar className="w-4 h-4 inline mr-1" />
                      Years of Experience
                    </label>
                    <input
                      type="number"
                      min="0"
                      max="50"
                      value={candidateInfo.years_experience}
                      onChange={(e) => setCandidateInfo(prev => ({ ...prev, years_experience: e.target.value }))}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                      placeholder="5"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Current Role
                    </label>
                    <input
                      type="text"
                      value={candidateInfo.current_role}
                      onChange={(e) => setCandidateInfo(prev => ({ ...prev, current_role: e.target.value }))}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                      placeholder="Senior Software Engineer"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Desired Role
                    </label>
                    <input
                      type="text"
                      value={candidateInfo.desired_role}
                      onChange={(e) => setCandidateInfo(prev => ({ ...prev, desired_role: e.target.value }))}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                      placeholder="Lead Software Engineer"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      <DollarSign className="w-4 h-4 inline mr-1" />
                      Salary Expectation
                    </label>
                    <input
                      type="text"
                      value={candidateInfo.salary_expectation}
                      onChange={(e) => setCandidateInfo(prev => ({ ...prev, salary_expectation: e.target.value }))}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                      placeholder="$120,000 - $150,000"
                    />
                  </div>
                </div>
              </div>

              {/* Additional Information */}
              <div className="mt-6 space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Availability
                  </label>
                  <select
                    value={candidateInfo.availability}
                    onChange={(e) => setCandidateInfo(prev => ({ ...prev, availability: e.target.value }))}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  >
                    <option value="">Select availability</option>
                    <option value="immediately">Immediately</option>
                    <option value="2_weeks">2 weeks notice</option>
                    <option value="1_month">1 month notice</option>
                    <option value="3_months">3 months notice</option>
                    <option value="flexible">Flexible</option>
                  </select>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    <Tag className="w-4 h-4 inline mr-1" />
                    Tags (comma-separated)
                  </label>
                  <input
                    type="text"
                    value={candidateInfo.tags}
                    onChange={(e) => setCandidateInfo(prev => ({ ...prev, tags: e.target.value }))}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    placeholder="frontend, react, senior, remote"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Notes
                  </label>
                  <textarea
                    value={candidateInfo.notes}
                    onChange={(e) => setCandidateInfo(prev => ({ ...prev, notes: e.target.value }))}
                    rows={3}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    placeholder="Additional notes about the candidate..."
                  />
                </div>
              </div>

              {/* Submit Button */}
              <div className="mt-8 flex justify-end space-x-4">
                <button
                  type="button"
                  onClick={() => navigate('/resume-bank')}
                  className="px-6 py-2 border border-gray-300 text-gray-700 rounded-md hover:bg-gray-50"
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  disabled={loading}
                  className="px-6 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  {loading ? 'Adding to Bank...' : 'Add to Resume Bank'}
                </button>
              </div>
            </form>
          )}
        </div>
      </div>
    </div>
  );
};

export default AddResume;
